var proton = this.getObjectByName("Proton");
var precession = this.getObjectByName("Precession");
var precess = false
var state = 0;
var start_y_time = 0;
var start_z_time = 0;

function update( event ) {
	var time = performance.now() / 1000;
	switch( state ) {
		case 0:
			break;
		case 1:
			// Show Precession
			proton.rotation.y = time - start_y_time;
			break;
		case 2:
			// Show effect of B0 field
			proton.rotation.y = time - start_y_time;
			proton.rotation.z = (time - start_z_time) / 5;
			break;
		case 3:
			// remove precession
			proton.rotation.z = (time - start_z_time) / 5;
			break;
		case 4:
			// demonstrate t1
			proton.rotation.z += -0.01 * ((time - start_z_time) / 5) * proton.rotation.z;
	};
}

function mouseup( event ) {
	switch( state ) {
		case 0:
			start_y_time = performance.now() / 1000;
			var quaternion = new THREE.Quaternion().setFromAxisAngle( new THREE.Vector3(0, 0, 1), Math.random(-Math.PI, Math.PI) );
			proton.applyQuaternion(quaternion);
			var precession_geometry = new THREE.Geometry();
			precession_geometry.vertices.push(new THREE.Vector3(0, 100, 0), new THREE.Vector3(0, -100, 0));
			var precession_axis = new THREE.Line(
				precession_geometry,
				new THREE.LineBasicMaterial({color: 0x00BCD4})
			);
			scene.add(precession_axis);
			break;
		case 1:
			start_z_time = performance.now() / 1000 - proton.rotation.z * 5;
			//console.log(proton.rotation);
			proton.children[3].visible = true;
			break;
		case 2:
			break;
		case 3:
			proton.children[3].visible = false;
			break;
		default:
			state=0;
	};
	state++;
	console.log(state);
}

function mousedown( event ) {
	switch( state ) {
		case 0:
			break;
		case 1:
			console.log(proton.rotation);
			break;
		case 2:
		default:
			break;
	};
}

