var proton = this.getObjectByName("Proton");
var precession = this.getObjectByName("Precession");
var sphere = this.getObjectByName("Sphere");
var Params = function() {
	this.timesteps = 1000;
	this.state = 0;
	this.next_state = function() { keyup( new KeyboardEvent('keyup', {'key': 'n'}) ) };
	this.prev_state = function() { keyup( new KeyboardEvent('keyup', {'key': 'b'}) ) };

	this.damping_factor = 0.005;
	this.rotating_frame = false;  // the actual controller
	this._rotating_frame = false;  // displayed on interface
	this.override_ref_frame = false;

	this.rot_x = 0;
	this.rot_y = 0;
	this.rot_z = 0;


};
var params = new Params();
var start_y_time = 0;
var start_z_time = 0;
var timesteps = 1000;
var camera = this.getObjectByName("Camera");

var gui = new dat.GUI({
    height : 5 * 32 - 1,
	width  : 480
});
var info = gui.addFolder('State Info');
info.add(params, 'timesteps').min(100).max(1000).step(50);
info.add(params, 'state').listen();
info.add(params, 'next_state');
info.add(params, 'prev_state');
info.add(params, '_rotating_frame').name("rot_ref").listen();
info.add(params, 'override_ref_frame').name("override_rot_ref");
info.add(params, 'damping_factor').min(0).max(0.1).step(0.005);
var rotation = gui.addFolder('Rotation (deg)');
rotation.add(params, 'rot_x').name("X").step(0.01).listen();
rotation.add(params, 'rot_y').name("Y").step(0.01).listen();
rotation.add(params, 'rot_z').name("Z").step(0.01).listen();
info.open();
rotation.open();

function init() {
	var precession_geometry = new THREE.Geometry();
	precession_geometry.vertices.push(new THREE.Vector3(0, 100, 0), new THREE.Vector3(0, -100, 0));
	var precession_axis = new THREE.Line(
		precession_geometry,
		new THREE.LineBasicMaterial({color: 0x00BCD4})
	);
	precession_axis.material.visible = false;
	precession_axis.material.transparent = true;
	precession_axis.name = "Precession Axis";
	scene.add(precession_axis);

	var axis = new THREE.AxesHelper(5)
	scene.add(axis);
}

init();

function update( event ) {
	var time = performance.now() / params.timesteps;
	params.rotation = proton.rotation;
	params.rot_x = (sphere.rotation.x 		% (2 * Math.PI))/Math.PI * 180;
	params.rot_y = (precession.rotation.y 	% (2 * Math.PI))/Math.PI * 180;
	params.rot_z = (proton.rotation.z 		% (2 * Math.PI))/Math.PI * 180;

	params.rotating_frame = params._rotating_frame || params.override_ref_frame;
	switch( params.state ) {
		case 0:
			break;
		case 1:
			// Show Precession
			// proton.rotation.y = time - start_y_time;
			break;
		case 2:
			// Show effect of B0 field
			// proton.rotation.y = time - start_y_time;
			proton.rotation.z = (time - start_z_time) / 5;
			break;
		case 3:
			// remove precession
			proton.rotation.z = (time - start_z_time) / 5;
			break;
		case 4:
			// demonstrate t1
			// proton.rotation.y = time - start_y_time;
			proton.rotation.z += -params.damping_factor * ((time - start_z_time) / 5) * Math.sin( proton.rotation.z );
			// this.getObjectByName("Precession Axis").material.opacity -= 0.001;
	};
	if( !params.rotating_frame ) {
		precession.rotation.y = time - start_y_time;	
	}
}

function keyup( event ) {
	// console.log("Button:" + event.button);
	// console.log(event.key);
	if( event.key == "n" ) {
		switch( params.state ) {
			case 0:
				start_y_time = performance.now() / params.timesteps;
				var quaternion = new THREE.Quaternion().setFromAxisAngle( new THREE.Vector3(0, 0, 1), Math.random(-Math.PI, Math.PI) );
				proton.applyQuaternion(quaternion);
				// this.getObjectByName("Precession Axis").material.visible = true;
				// this.getObjectByName("Precession Axis").material.opacity = 1;
				break;
			case 1:
				start_z_time = performance.now() / params.timesteps - proton.rotation.z * 5;
				//console.log(proton.rotation);
				proton.children[3].visible = true;
				break;
			case 2:
				params._rotating_frame = true;
				break;
			case 3:
				proton.children[3].visible = false;
				start_y_time = performance.now() / params.timesteps - precession.rotation.y;
				params._rotating_frame = false;
				break;
			default:
				 params.state = 0;
		};
		params.state++;
	}
	if ( event.key == "b" ) {
		switch(  params.state ) {
			case 0:
				break;
			case 1:
				params.state = 0;
				proton.rotation.z = 0;
				proton.rotation.y = 0;
				// this.getObjectByName("Precession Axis").material.visible = false;
				start_y_time = 0;
				break;
			case 2:
				params._rotating_frame = false;
				proton.children[3].visible = false;
				start_z_time = 0;
				params.state = 1;
				break;
			case 3:
				params._rotating_frame = true;
				proton.children[3].visible = true;
				start_y_time = performance.now() / params.timesteps - precession.rotation.y;
				params.state = 2;
				break;

		};
	}
	console.log(params.state, proton.rotation);
}

